from datetime import datetime, timedelta

class xBeachModel():

    def __init__(self):
        
        self.preProcessed = False

        self.inTimeSeries = None
        self.watlevSeries = None

        self.waterlevFile = None
        self.dfGauges = None

    def setFEWSargs(self,
                    runName=None,
                    sysTime=None,
                    roundedTime=None,
                    spinUpWindow=None,
                    modelWorkDir=None,
                    forecastHorizon=None,
                    deltat=None,
                    siteName=None):
        
        self.runName = runName
        self.systemTime = sysTime
        self.roundedTime = roundedTime
        self.spinUpWindow = timedelta(hours=int(spinUpWindow)) # in hours, datetime object
        self.modelWorkDir = modelWorkDir
        self.forecastHorizon = timedelta(days=int(forecastHorizon)) # in days, datetime object
        self.deltat = timedelta(minutes=int(deltat))
        self.siteName = siteName

        # More attributes based on these inputs

        # Set start time based on rounded time and spinup window
        self.startTime = self.roundedTime - self.spinUpWindow
        self.endTime = self.startTime + self.spinUpWindow + self.forecastHorizon
